let cur_topic_index = -1;
let topics_list_length = 0;

var topics_list = global.get("state_topics_list");
var payload_list = global.get("state_payload_list");
var new_payload = [];

for (var topics_list_index in topics_list) {
    if ( (topics_list[topics_list_index].indexOf(msg.topic) >= 0) && (topics_list[topics_list_index].length == msg.topic.length) ) {
        cur_topic_index = topics_list_index;
        break;
    }
    topics_list_length++;
}

if (cur_topic_index > -1) {
    payload_list[cur_topic_index] = msg.payload;
} else {
    topics_list.push(msg.topic);
    payload_list.push(msg.payload);
}

global.set("state_topics_list", topics_list);
global.set("state_payload_list", payload_list);


for (var index in payload_list) {
    new_payload.push({'Variable': topics_list[index].substr(16, topics_list[index].length), 'Value': payload_list[index]});
}

msg.payload = new_payload;
return msg;

